import { useEffect } from 'react';
import styled from 'styled-components';

export type LetterProps = {
    letter: string;
    points: string;
    leftPos: string;
}

let idCounter = 0;

export const LetterTile = (props : LetterProps) => {

    const id = idCounter++;

    const style = {
        left: props.leftPos+"px"
    }

    return (
        <div draggable="true" className="letterTile" id={"letterTile_"+idCounter++}>
            {props.letter}
            <div className="letterPoint">
                {props.points}
            </div>
        </div>
    );
};
