import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { LoginComponent } from './components/LoginComponent';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import { GameBoardComponent } from './components/GameBoardComponent';
import styled from 'styled-components';
import Player from './types/player';
import { Lobby } from './components/Lobby';
/*
TODO

GAME STATE
*Restart
*End turn
 -Player turn indicator
 -Disable components
*Update points

TILES
*Draw a tile
*Put back a tile
*Put a tile on board


*/

function App() {

  return (
    <Router>
      <div>
        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/lobby">
            <Lobby></Lobby>
          </Route>
          <Route path="/main">
            <GameViewHeader></GameViewHeader>
            <GameBoardComponent></GameBoardComponent>
          </Route>
          <Route path="/">
            <LoginComponent></LoginComponent>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

const GameViewHeader = () => {

  const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-evenly;
  flex-direction: row;`;

  return (
    <HeaderContainer>
      <PlayerInfo name="Olli"></PlayerInfo>
      <PlayerInfo name="Matti"></PlayerInfo>
    </HeaderContainer>
  )
}
const PlayerInfo = (props) => {

  const PlayerContainer = styled.div`
  border: 1px solid black;
    padding: 10px;
    min-width: 57px;
    padding-left: 15px;
    display: flex;
    flex-direction: column;
    background-color: white;
  & > div {
    display: inline-block;
  }`;

  const PointButton = styled.button`
    height: 100%;
  `;

  const FlexDivRow = styled.div`
  display: flex;
  flex-direction: row;
  `;

  const FlexDivCol = styled.div`
  display: flex;
  flex-direction: column;
  `;

  const [points, setPoints] = useState(0);

  return (
    <FlexDivRow>
      <PlayerContainer>
        <div>{props.name}</div>
        <div>{points+"pts"}</div>
      </PlayerContainer>
      <FlexDivCol>
        <PointButton onClick={() => {setPoints(points+1);} }>+</PointButton>
        <PointButton onClick={() => {setPoints(points-1);} }>-</PointButton>
      </FlexDivCol>
    </FlexDivRow>
  )
}

export default App;
