import axios from 'axios';
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { BE_URL } from '../state';
import { BoardTileComponent } from './BoardTileComponent';
import { HandComponent } from './HandComponent';
import { PouchComponent } from './PouchComponent';
import { BoardTile, GameState } from '../../../scrabble-backend/server';
import { LetterTile } from '../types/types';

export const GameBoardComponent = () => {

    const initialGameState = {
        playerPoints: [],
        turnOfPlayer: "",
        pouchLetters: [],
        board: []
    }

    const [ gameState, setGameState ] = useState<GameState>(initialGameState);
    const [board , setBoard] = useState<BoardTile[][]>([]);

    useEffect( () => {

        axios.get(BE_URL+"/game").then( (resp) => {
            //setGameState(resp.data);
            setBoard(resp.data.board);
        });
    },[]);

    const handleLetterTileDrop = async (letterTile : LetterTile, x : number, y : number) => {
        const newBoard = [...gameState.board];
        newBoard[x][y].letter = letterTile.letter;
        newBoard[x][y].points = letterTile.points;
        gameState.board = newBoard;
        //const stateFromBe = await sendGameStateToBE(gameState);
        //setGameState(gameState);
        setBoard(newBoard);
    };

    const sendGameStateToBE = async (state : GameState) => {
        const resp = await axios.post(BE_URL+"/game",{ game: state });
        return resp.data;
    };

    return (
		<div>
        	<BoardComponent>
            	{board.map( (x,y) => ( 
                    x.map( (tile,i) => (<BoardTileComponent letterTile={ tile.letter ? {letter: tile.letter, points: tile.points} : undefined } tileXPos={y} tileYPos={i} tileDropped={handleLetterTileDrop} tileType={tile.tileType}></BoardTileComponent>))
                ))}
        	</BoardComponent>
			<ButtomContainer>
				<HandComponent/>
				<PouchComponent/>
			</ButtomContainer>
		</div>
    );
}

