import axios from 'axios';
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { BE_URL } from '../state';
import { BoardTileComponent } from './BoardTileComponent';
import { HandComponent } from './HandComponent';
import { PouchComponent } from './PouchComponent';
import { BoardTile, GameState, LetterTile } from '../../../scrabble-backend/server';
import { getCurrentPlayerName } from '../util/utils';
import { useGameState } from '../util/GameStateProvider';

export const GameBoardComponent = () => {

    const { gameState, setGameState, sendGameStateToBE, moveLetterFromHandToBoard } = useGameState();

    const handleLetterTileDrop = async (letterTile : LetterTile, x : number, y : number) => {
        
        moveLetterFromHandToBoard(letterTile, x, y);
    };

    const copyPlayerHand = (tile : LetterTile) => {
        if( !gameState.playerHands[getCurrentPlayerName()] ) return [];
        return [...gameState.playerHands[getCurrentPlayerName()]];
    }
    
    return (
		<div>
        	<BoardComponent>
            	{gameState.board.map( (x,y) => ( 
                    x.map( (tile,i) => (
                    <BoardTileComponent 
                    letterTile={ tile.letter ? {letter: tile.letter, points: tile.points} : undefined } 
                    tileXPos={y} 
                    tileYPos={i} 
                    tileDropped={handleLetterTileDrop} 
                    tileType={tile.tileType}>
                    </BoardTileComponent>))
                ))}
        	</BoardComponent>
			<ButtomContainer>
				<HandComponent letters={gameState.playerHands[getCurrentPlayerName()] !== undefined ? gameState.playerHands[getCurrentPlayerName()] : []}/>
				<PouchComponent letters={gameState.pouchLetters} />
			</ButtomContainer>
		</div>
    );
}

