import { useEffect } from 'react';
import styled from 'styled-components';
import { LetterTile } from '../../../scrabble-backend/types';
import { LetterTileComponent } from './LetterTileComponent';

let idCounter = 0;

const getHandLetters = () : LetterTile[] => {
    return [
        { letter: "A", points: 2},
        { letter: "B", points: 10},
        { letter: "C", points: 12},
    ]
}

export const HandComponent = () => {

    const calculateLeftPos = (i) => {
        const leftPosNumber = i * 40;
        return ""+leftPosNumber;
    }

    return (
        <Div>
            {getHandLetters().map( (letter, idx) => (
               <LetterTileComponent letter={letter} leftPos={calculateLeftPos(idx)}></LetterTileComponent> 
            ))}
        </Div>
    )
}

const Div = styled.div`
margin-top: 10px;
height: 50px;
width: 500px;
background-color: #989a8e;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
`;
