import { useEffect } from 'react';
import styled from 'styled-components';
import { LetterTile } from '../../../scrabble-backend/types';
import { BoardTile, X3W, N0R, X2W, X3L, X2L, CNT } from './BoardTile';
import { HandComponent } from './HandComponent';
import { PouchComponent } from './PouchComponent';


const boardTiles = [
        [X3W,N0R,N0R,X2W,N0R,N0R,N0R,X3W,N0R,N0R,X2W,N0R,N0R,N0R,X3W],
		[N0R,X2W,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,X2W,N0R],
		[N0R,N0R,X2W,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,X2W,N0R,N0R],
		[N0R,N0R,N0R,X2W,N0R,N0R,N0R,N0R,N0R,N0R,N0R,X2W,N0R,N0R,N0R],
		[N0R,N0R,N0R,N0R,X2W,N0R,N0R,N0R,N0R,N0R,X2W,N0R,N0R,N0R,N0R],	
		[N0R,N0R,N0R,N0R,N0R,X3L,N0R,N0R,N0R,X3L,N0R,N0R,N0R,N0R,N0R],
		[N0R,N0R,N0R,N0R,N0R,N0R,X2L,N0R,X2L,N0R,N0R,N0R,N0R,N0R,N0R],
		[N0R,N0R,N0R,N0R,N0R,N0R,N0R,CNT,N0R,N0R,N0R,N0R,N0R,N0R,N0R],
		[N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R,N0R],
