import { useEffect } from 'react';
import styled from 'styled-components';
import { LetterTile } from '../../../scrabble-backend/types';
import { LetterTileComponent } from './LetterTileComponent';

let idCounter = 0;

export const HandComponent = (props) => {

    return (
        <Div>
            {props.letters.map( (letter) => (
               <LetterTileComponent letter={letter} leftPos="0"></LetterTileComponent> 
            ))}
        </Div>
    )
}

const Div = styled.div`
margin-top: 10px;
height: 50px;
width: 500px;
background-color: #989a8e;
border-top-left-radius: 5px;
border-top-right-radius: 5px;
`;
