import axios from "axios";
import { useCallback, useEffect, useState } from "react";
import { BoardTile, GameState } from "../../../scrabble-backend/server";
import { BE_URL } from "../state";

const initialGameState = {
    playerPoints: [],
    turnOfPlayer: "",
    pouchLetters: [],
    board: [],
    playerHands: {}
}

export const useGameState = () => {

    const [gameState, setGameState] = useState<GameState>(initialGameState);
    const [board, setBoard] = useState<BoardTile[][]>([]);

    const fetchGameStateFromBe = useCallback( () => {
        axios.get(BE_URL + "/game").then((resp) => {
            setGameState(resp.data);
            setBoard(resp.data.board);
        })
    },[]);

    useEffect(() => {
        fetchGameStateFromBe();
    }, [fetchGameStateFromBe]);

    return {
        gameState : gameState,
        setGameState : setGameState,
        board: board,
        setBoard: setBoard,
        fetchGameStateFromBe : fetchGameStateFromBe
    }
}
