import { useEffect } from 'react';
import styled from 'styled-components';

export const LoginComponent = () => {

    const handleLogin = () => {

        const usernameInput = document.getElementById("txtUsername") as HTMLInputElement;
        const passwordInput = document.getElementById("txtUsername") as HTMLInputElement;
        if( passwordInput.value == 'kala')
            window.location.href = '/users';
    };

    return (
        <LoginPanel>
            <TextField id="txtUsername" />
            <TextField id="txtPassword" />
            <Button onClick={ handleLogin }>Login</Button>
        </LoginPanel>
    );
}

const LoginPanel = styled.div`
width: 210px;
border-radius: 6px;
padding: 24px;
background: #1e2529;
`;

const TextField = styled.input.attrs(props => ({
    type: "password"
}))`
height: 22px;
padding: 5px;
border: 2px solid 2196f3;
margin-top: 5px;
`;

const Button = styled.button`
    display: inline-block;
    height: 30px;
    background: #2196f3;
    border-radius: 4px;
    border: none;
    margin-top: 5px;
    width: 181px;

    &:hover {
      opacity: 0.8;
    }

    &:active {
        border: 1px;
        border-style: inset;
        user-select: none;
    }
`;
