import { useEffect } from 'react';
import styled from 'styled-components';
import { LetterTile } from '../types/types';

export type LetterProps = {
    letter : LetterTile;
    leftPos: string;
}

let idCounter = 0;

export const LetterTileComponent = (props : LetterProps) => {

    const id = idCounter++;

    const style = {
        left: props.leftPos+"px"
    }

    const handleDragStart = (e) => {
        e.dataTransfer.setData("text", e.target.id);
    }

    return (
        <div onDragStart={ handleDragStart } draggable="true" className="letterTile" id={"letterTile_"+idCounter++}>
            {props.letter.letter}
            <div className="letterPoint">
                {props.letter.points}
            </div>
        </div>
    );
};
