    X2L = 3, 
    X3L = 4,
    CNT = 5;

export const BoardTile = (props) => {

    const tileStaticStyle = {
        fontSize: "11px"
    }

    const getTileStyleFromProps = (tileType) => {
        if( tileType === X2W )
            return {
                backgroundColor: "pink",
                fontSize: "11px"
            }
        else if( tileType === X3W )
            return {

            }
        else if( tileType == X2L )
            return "2xLetter";
        else if( tileType == X3L )
            return "3xLetter";
        else if( tileType == CNT )
            return "3xLetter";
    };

    const getTileTextFromProps = (tileType) => {

        if( tileType == X2W )
            return "2xWord";
        else if( tileType == X3W )
            return "3xWord";
        else if( tileType == X2L )
            return "2xLetter";
        else if( tileType == X3L )
            return "3xLetter";
        
        return "";
    }

    return (
        <BoardTileDiv style={styleDoubleWord}></BoardTileDiv>
    )
}

const BoardTileDiv = styled.div`
    background-color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(0, 0, 0, 0.8);
    text-align: center;
    padding: 4px;
    width: 45px;
    height: 45px;
`;


