import axios from 'axios';
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { BE_URL } from '../state';
import { BoardTileComponent } from './BoardTileComponent';
import { HandComponent } from './HandComponent';
import { PouchComponent } from './PouchComponent';
import { BoardTile, GameState, LetterTile } from '../../../scrabble-backend/server';
import { getCurrentPlayerName } from '../util/utils';
import { useGameState } from '../util/GameStateProvider';

export const GameBoardComponent = () => {

    const { board, setBoard, gameState, setGameState, sendGameStateToBE } = useGameState();

    const handleLetterTileDrop = async (letterTile : LetterTile, x : number, y : number) => {
        //const newBoard = [...gameState.board];
        const newBoard = [...board];
        newBoard[x][y].letter = letterTile.letter;
        newBoard[x][y].points = letterTile.points;
        gameState.board = newBoard;


        // Remove moved tile from hand
        const newHand = copyPlayerHand(letterTile);
        removeTileFromPlayerHand(newHand, letterTile);
        updatePlayerHandInGameState(newHand);

        const stateFromBe = await sendGameStateToBE(gameState);
        //setGameState(gameState);
        setBoard(newBoard);
    };

    const updatePlayerHandInGameState = (hand : LetterTile[]) => {
        if( !gameState.playerHands[getCurrentPlayerName()] ) return;
        gameState.playerHands[getCurrentPlayerName()] = hand;
    }

    const copyPlayerHand = (tile : LetterTile) => {
        if( !gameState.playerHands[getCurrentPlayerName()] ) return [];
        return [...gameState.playerHands[getCurrentPlayerName()]];
    }

    const removeTileFromPlayerHand = (hand : LetterTile[], letterTile : LetterTile) => {
        if( !hand || hand.length === 0 ) return;

        const tileIdx = hand.findIndex( (tile, idx) => tile.id === letterTile.id);
        if( tileIdx === -1 ) return;
        hand.splice(tileIdx,1);
    }
    
    return (
		<div>
        	<BoardComponent>
            	{board.map( (x,y) => ( 
                    x.map( (tile,i) => (
                    <BoardTileComponent 
                    letterTile={ tile.letter ? {letter: tile.letter, points: tile.points} : undefined } 
                    tileXPos={y} 
                    tileYPos={i} 
                    tileDropped={handleLetterTileDrop} 
                    tileType={tile.tileType}>
                    </BoardTileComponent>))
                ))}
        	</BoardComponent>
			<ButtomContainer>
				<HandComponent/>
				<PouchComponent pouchLetters={gameState.pouchLetters}/>
			</ButtomContainer>
		</div>
    );
}

