import axios from 'axios';
import { useEffect, useState } from 'react';
import styled from 'styled-components';
import { LetterTile } from '../../../scrabble-backend/types';
import { BE_URL } from '../state';
import { X3W, N0R, X2W, X3L, X2L, CNT, BoardTileComponent } from './BoardTileComponent';
import { HandComponent } from './HandComponent';
import { PouchComponent } from './PouchComponent';
import { BoardTile } from '../../../scrabble-backend/server';

export const GameBoardComponent = () => {

    const [ boardTiles, setBoardTiles ] = useState<BoardTile[][]>([]);

    const fetchBoard = async () => {
        const resp = await axios.get(BE_URL+"/game");
        return JSON.parse(resp.data.board);
    }

    useEffect( () => {

        fetchBoard().then( (data) => {
            setBoardTiles(data);
        });
    });

    return (
		<div>
        	<BoardComponent>
            	{boardTiles.map( (x,y) => (
                    x.map( tile => (<BoardTileComponent tileType={tile.tileType}></BoardTileComponent>))
                ))}
        	</BoardComponent>
			<ButtomContainer>
				<HandComponent/>
				<PouchComponent/>
			</ButtomContainer>
		</div>
    );
}

