]

const letters = [
    'A','A','A','A','A','A','A','A','A',
		'B','B',
		'C',
		'D','D','D','D',
		'E','E','E','E','E','E','E','E','E','E','E','E',
		'F','F',
		'G',
		'H','H','H',
		'I','I','I','I','I','I','I','I','I',
		'J',
		'K','K','K',
		'L','L','L',
		'M','M',
		'N','N','N','N','N','N',
		'O','O','O','O','O','O','O','O',
		'P','P',
		'R','R','R','R',
		'S','S','S','S','S',
		'T','T','T','T','T','T',
		'U','U','U','U',
		'V','V',
		'X',
		'Y','Y',
		'Z',
		'-','-',
		'Ä','Ä','Ä',
		'Ö','Ö'
];

const points = [];
points["A"] = 1;
	points["B"] = 8;
	points["C"] = 10;
	points["D"] = 7;
	points["E"] = 1;
	points["F"] = 8;
	points["G"] = 8;
	points["H"] = 4;
	points["I"] = 1;
	points["J"] = 4;
	points["K"] = 2;
	points["L"] = 2;
	points["M"] = 3;
	points["N"] = 1;
	points["O"] = 2;
	points["P"] = 4;
	points["Q"] = 10;
	points["R"] = 4;
	points["S"] = 1;
	points["T"] = 1;
	points["U"] = 3;
	points["V"] = 4;
	points["X"] = 8;
	points["Y"] = 4;
	points["Z"] = 10;
	points["Ä"] = 2;
	points["Ö"] = 3;

export const GameBoardComponent = () => {

    return (
		<div>
        	<BoardComponent>
            	{boardTiles.map( (x,y) => (
                    x.map( tile => (<BoardTile tileType={tile}></BoardTile>))
                ))}
        	</BoardComponent>
			<HandComponent></HandComponent>
		</div>
    );
}

const HandComponent = styled.div`
margin-left: 10px;
margin-right: 10px;
height: 50px;
width: 500px;
background-color: cyan;
`;

const BoardComponent = styled.div`
display: grid;
grid-template-columns: auto auto auto auto auto auto auto auto auto auto auto auto auto auto auto;
background-color: #2196F3;
padding: 2px;
width: 718px;
margin: auto;
padding: 10px;
`;
