import { useEffect } from 'react';
import styled from 'styled-components';

export const N0R = 0, 
    X2W = 1, 
    X3W = 2, 
    X2L = 3, 
    X3L = 4,
    CNT = 5;

export const BoardTileComponent = (props) => {

    const getTileStyle = (tileType) => {

        return {
            fontSize: "11px",
            background: getTileColorFromProps(tileType)
        }
    }
    
    const getTileColorFromProps = (tileType) => {

        if( tileType == X2W )
            return "pink";
        else if( tileType == X3W )
            return "red";
        else if( tileType == X2L )
            return "cyan";
        else if( tileType == X3L )
            return "blue";
        else if( tileType == CNT )
            return "red";
        
        return "";
    }

    const getTileTextFromProps = (tileType) => {

        if( tileType == X2W )
            return "2xWord";
        else if( tileType == X3W )
            return "3xWord";
        else if( tileType == X2L )
            return "2xLetter";
        else if( tileType == X3L )
            return "3xLetter";
        
        return "";
    }

    return (
        <BoardTileDiv style={getTileStyle(props.tileType)}>{getTileTextFromProps(props.tileType)}</BoardTileDiv>
    )
}

const BoardTileDiv = styled.div`
    background-color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(0, 0, 0, 0.8);
    text-align: center;
    padding: 4px;
    width: 38px;
    height: 38px;
`;
